var queues = {};
var eventQueues = {};

( async () => {
    await node.send([{
        topic: "status",
        payload: {
            fill: "red",
            shape: "ring",
            text: "Starting Queue Containers"
        }
    }]);
    await global.set("sendJobToQueue", async (queueName, jobName, jobData, jobOptions = {}) => {
        if (!queues[queueName]) {
            queues[queueName] = await new Queue(queueName, { connection });
            eventQueues[queueName] = await new QueueEvents(queueName, { connection });
        };
        var job = await queues[queueName].add(jobName, jobData, Object.assign({ removeOnComplete: 1000, removeOnFail: 5000 }, jobOptions));
        const result = await job.waitUntilFinished(eventQueues[queueName]);
        return result
    });
    await global.set("getQueue", async (queueName) => {
        if (!queues[queueName]) {
            queues[queueName] = await new Queue(queueName, { connection });
            eventQueues[queueName] = await new QueueEvents(queueName, { connection });
            node.send([{
                topic: "status",
                payload: {
                    fill: "red",
                    shape: "ring",
                    text: "Starting Queue: " + queueName
                }
            }]);
        };
        let queueJob = async (jobName, jobData, jobOptions = {}) => {
            var job = await queues[queueName].add(jobName, jobData, jobOptions);
            const result = await job.waitUntilFinished(eventQueues[queueName]);
            return result
        };
        return queueJob
    });
    await node.send([{
        topic: "status",
        payload: {
            fill: "red",
            shape: "ring",
            text: "Running Queue Containers "
        }
    }]);
    node.send([null,{}])
})()

