const { Queue, QueueEvents } = bullmq;
const IORedis = ioredis;

const connection = new IORedis(env.get("REDIS_URL"), { maxRetriesPerRequest: null });
const AsyncFunction = (async function () { }).constructor;

var newQueue;
var newQueueEvents;

context.set("createNewQueue", async (name) => {
    newQueue = await new Queue(name, { connection });
    newQueueEvents = await new QueueEvents(name, { connection })
    await context.set("addJob", async (jobName, jobData) => {
        var job = await newQueue.add(jobName, jobData);
        const result = await job.waitUntilFinished(newQueueEvents);
        return result
    })
})

