// Lấy trạng thái cũ từ flow context, nếu không có thì là đối tượng rỗng
// Dùng context.file để lưu trữ bền bỉ
let oldState = flow.get('uploadsState', 'file') || {};

// --- HÀM: Lấy trạng thái hiện tại của thư mục ---
function getCurrentState(dir) {
    const state = {};
    try {
        const files = fs.readdirSync(dir, { withFileTypes: true });
        for (const file of files) {
            const fullPath = path.join(dir, file.name);
            try {
                const stats = fs.statSync(fullPath);
                const relativePath = path.relative(uploadDir, fullPath);
                state[relativePath] = {
                    isDirectory: stats.isDirectory(),
                    size: stats.isFile() ? stats.size : 0,
                    mtimeMs: stats.mtimeMs,
                    fullPath: fullPath
                };
                if (stats.isDirectory()) {
                    Object.assign(state, getCurrentState(fullPath));
                }
            } catch (error) { // Bỏ qua file bị xóa trong lúc quét
                if (error.code !== 'ENOENT') throw error;
            }
        }
    } catch(e) { node.error(e) }
    return state;
}

// --- HÀM: So sánh hai trạng thái để phát hiện thay đổi ---
function compareStates(oldState, newState) {
    const changes = { new: [], deleted: [], modified: [] };
    for (const filePath in newState) {
        if (!oldState[filePath]) {
            changes.new.push(newState[filePath]);
        } else {
            if (newState[filePath].size !== oldState[filePath].size ||
                newState[filePath].mtimeMs !== oldState[filePath].mtimeMs) {
                changes.modified.push(newState[filePath]);
            }
        }
    }
    for (const filePath in oldState) {
        if (!newState[filePath]) {
            changes.deleted.push(oldState[filePath]);
        }
    }
    return changes;
}

// --- LOGIC CHÍNH ---
