let BullMQAdapter = bullBoardApiBullMQAdapter.BullMQAdapter;
let ExpressAdapter = bullBoardExpress.ExpressAdapter;

var queueNameSet = new Set();

async function discoverQueues() {
    const keys = await redis.keys('bull:*:id');
    const queueNames = keys.map(k => k.split(':')[1]);
    const uniqueQueueNames = Array.from(new Set(queueNames)).filter(name => !queueNameSet.has(name));
    queueNameSet = new Set([...queueNameSet, ...uniqueQueueNames]);
    return uniqueQueueNames.map(name => new Queue(name, { connection: redis }));
}

