flow.set("sendJobToQueue", async (queueName, jobName, jobData, jobOptions = {}) => {
    if (!queues[queueName]) {
        queues[queueName] = await new Queue(queueName, { connection });
        eventQueues[queueName] = await new QueueEvents(queueName, { connection });
    };
    var job = await queues[queueName].add(jobName, jobData, jobOptions);
    const result = await job.waitUntilFinished(eventQueues[queueName]);
    return result
});

