const { EventEmitter } = events;
const { ApolloServer } = apolloServer;
const { createId } = paralleldriveCuid2;

const emitter = new EventEmitter();
emitter.setMaxListeners(0);

const OperationMap = new Map([
    ["ne", "not"],
    ["eq", "equals"],
    ["gt", "gt"],
    ["ge", "gte"],
    ["lt", "lt"],
    ["le", "lte"],
    ["in", "in"],
    ["nin", "notIn"],
    ["ct", "contains"],
    ["sw", "startsWith"],
    ["ew", "endsWith"],
    ["and", "and"],
    ["not", "not"],
    ["or", "or"],
]);

const ArgsDefaultMap = new Map([
    ["where", {}]
])

const parseArguments = function (args, variables) {
    function parseVariable(value) {
        if (value && typeof value == "object") {
          if (Object.prototype.toString.call(value) === '[object Object]') {
                return Object.fromEntries(Object.entries(value).map(([key, val]) => [OperationMap.get(key) || key, parseVariable(val)]));
            } else if (Object.prototype.toString.call(value) === '[object Array]') {
                return value.map((val) => parseVariable(val));
            }
        } else {
            return value;
        }
    }

    function parseValue(value) {
        if (value.kind == "Variable") {
            return parseVariable(variables[value.name.value]);
        }
    };
    let re = Object.fromEntries(args.map((arg) => ([arg.name.value, parseValue(arg.value) || ArgsDefaultMap.get(arg.name.value)])));
    return re;
};

class Build_graphQlServer {
    constructor({ tables, path }) {
        this.path = path;
        this.tables = tables;
        this.typeDefType = new Map([
            ["0", "ID"],
            ["1", "Int"],
            ["2", "Float"],
            ["3", "String"],
            ["4", "Boolean"],
            ["5", "Date"],
            ["6", "Json"],
        ]);

        this.sendAndListenQueue;
        this.initTypeDefs = `
                scalar Date
                scalar Json
                scalar asc
                scalar desc

                input ModelIntWhereInput {
                  _neq: Int
                  _eq: Int
                  _lte: Int
                  _lt: Int
                  _gte: Int
                  _gt: Int
                  _in: [Int]
                  _nin: [Int]
                  _is_null: Boolean
                }

                input ModelFloatWhereInput {
                  _neq: Float
                  _eq: Float
                  _lte: Float
                  _lt: Float
                  _gte: Float
                  _gt: Float
                  _in: [Float]
                  _nin: [Float]
                  _is_null: Boolean
                }

                input ModelStringWhereInput {
                  _neq: String
                  _eq: String
                  _like: String
                  _ilike: String
                  _regex: String
                  _iregex: String
                  _similar: String
                  _in: [String]
                  _nin: [String]
                  _is_null: Boolean
                }

                input ModelBooleanWhereInput {
                  _not: Boolean
                  _eq: Boolean
                  _in: [Boolean]
                  _nin: [Boolean]
                  _is_null: Boolean
                }

                input ModelDateWhereInput {
                  _neq: Date
                  _eq: Date
                  _lte: Date
                  _lt: Date
                  _gte: Date
                  _gt: Date
                  _in: [Date]
                  _nin: [Date]
                  _is_null: Boolean
                }

                input ModelJsonCastInput {
                  String: ModelStringWhereInput
                }

                input ModelJsonWhereInput {
                  _cast: ModelJsonCastInput
                  _eq: Json
                }

                input ModelListStringWhereInput {
                  _contains: String
                  _eq: [String]
                  _has_keys_all: [String]                                                               
                  _has_keys_any: [String]
                  _is_null: Boolean
                }

                input ModelListIntWhereInput {
                  _contains: Int
                  _eq: [Int]
                  _has_keys_all: [Int]                                                               
                  _has_keys_any: [Int]
                  _is_null: Boolean
                }

                input ModelListFloatWhereInput {
                  _contains: Float
                  _eq: [Float]
                  _has_keys_all: [Float]                                                               
                  _has_keys_any: [Float]
                  _is_null: Boolean
                }

                input ModelListDateWhereInput {
                  _contains: Date
                  _eq: [Date]
                  _has_keys_all: [Date]                                                               
                  _has_keys_any: [Date]
                  _is_null: Boolean
                }

                enum ModelOrderByInput {
                  asc
                  desc
                }
            `;
        this.tables = new Map(tables.map((x) => [x.id, x]));
    }

    setQueue(func) {
        this.sendAndListenQueue = func;
    }

    generateTypeDefs() {
        return [
            this.initTypeDefs,
            ...[...this.tables.values()].map(({ id, schema, columns, column }) => {
                return column
                    ? `
            type ${id} {
              id: String
              createdAt: Date
              updatedAt: Date
              ${columns.map(({ id, type, required, is_many }) => `${id}: ${is_many ? "[" : ""}${this.typeDefType.has(type) ? this.typeDefType.get(type) : type}${is_many ? "]" : ""}${required ? "!" : ""}`).join("\n\t")}
          }`
                    : `
        type ${id} {
            id: String
            createdAt: Date
            updatedAt: Date
            ${columns
                        .filter(({ relations, type }) => [...(relations || [])].length == 0 && this.typeDefType.get(type))
                        .map(({ id, type, required, is_many }) => `${id}: ${is_many ? "[" : ""}${this.typeDefType.get(type)}${is_many ? "]" : ""}${required ? "!" : ""}`)
                        .join("\n\t")}
            ${columns
                        .filter(({ relations, type }) => (relations?.length && this.tables.has(type)))
                        .map(({ id, type, required, is_many }) => `list_${type} (where: Model${type}WhereInput, distinctOn: [Model${type}ColumnInput], limit: Int, offset: Int, orderBy: Model${type}OrderByInput): [${type}]`)
                        .join("\n\t")}
        }

        ${(() => {
                        let col_number_ids = columns.filter(({ type }) => ["1", "2"].includes(type)).map(x => x.id);
                        return col_number_ids > 0 ?
                            `type ${id}_is_number {
            ${col_number_ids.join("\n\t")}
          }

          type ${id}_aggregate_object {
            count: Int
            ${["sum", "avg", "stddev", "stddev_pop", "stddev_samp", "var_pop", "var_samp", "variance"].map(operation => `${operation}: type ${id}_is_number`).join("\n\t")}
          }

          ` : `
          type ${id}_aggregate_object {
            count: Int
          }
          `

                    })()}

        type ${id}_aggregate {
          aggregate: ${id}_aggregate_object
          nodes: [${id}]
        }

        input Model${id}WhereInput {
            id: ModelStringWhereInput
            createdAt: ModelDateWhereInput
            updatedAt: ModelDateWhereInput
            and: [Model${id}WhereInput]
            or: [Model${id}WhereInput]
            not: Model${id}WhereInput
            ${columns
                        .filter(({ relations, type }) => [...(relations || [])].length == 0 && this.typeDefType.get(type))
                        .map(({ id, type, required, is_many }) => `${id}: Model${this.typeDefType.get(type)}WhereInput`)
                        .join("\n\t")}
            ${columns
                        .filter(({ relations, type }) => (relations?.length && this.tables.has(type)))
                        .map(({ id, type, required, is_many }) => `list_${type}: Model${type}WhereInput`)
                        .join("\n\t")}
        }

        input Model${id}OrderByInput {
          id: ModelOrderByInput
          createdAt: ModelOrderByInput
          updatedAt: ModelOrderByInput
          ${columns
                        .filter(({ relations, type }) => [...(relations || [])].length == 0 && this.typeDefType.get(type))
                        .map(({ id, type, required, is_many }) => `${id}: ModelOrderByInput`)
                        .join("\n\t")}
        }

        enum Model${id}ColumnInput {
          id
          createdAt
          updatedAt
          ${columns
                        .filter(({ relations, type }) => [...(relations || [])].length == 0 && this.typeDefType.get(type))
                        .map(({ id, type, required, is_many }) => `${id}`)
                        .join("\n\t")}
        }

        ${(() => {
                        let _columns = columns.filter(({ relations, type }) => [...(relations || [])].length == 0 && this.typeDefType.get(type));
                        _columns = _columns.map(({ id: column_id, type, required, is_many }) => `${column_id}: ${is_many ? "[" : ""}${this.typeDefType.has(type) ? this.typeDefType.get(type) : type}${is_many ? "]" : ""}${(required && !id.endsWith("history")) ? "!" : ""}`);

                        return `input Model${id}CreateInput {
            ${["version: String", ..._columns].join("\n\t")}
          }`;
                    })()}

        ${(() => {
                        let _columns = columns.filter(({ relations, type, no_modify }) => !no_modify && [...(relations || [])].length == 0 && this.typeDefType.get(type));
                        _columns = _columns.map(({ id, type, required, is_many }) => `${id}: ${is_many ? "[" : ""}${this.typeDefType.has(type) ? this.typeDefType.get(type) : type}${is_many ? "]" : ""}`);
                        return `input Model${id}UpdateInput {
           ${["version: String", ..._columns].join("\n\t")} 
          }`;
                    })()}

        ${(id.endsWith("_history") || (schema !== this.path)) ? '' : `type Query {
          get_${id} (id: ID!): ${id}!
          list_${id} (where: Model${id}WhereInput, distinctOn: [Model${id}ColumnInput], limit: Int, offset: Int, orderBy: Model${id}OrderByInput): [${id}]!
          aggregate_${id} (where: Model${id}WhereInput): ${id}_aggregate!
        }

        type Mutation {
          create_${id} (input: Model${id}CreateInput!): ${id}!
          update_${id} (input: Model${id}UpdateInput!, id: ID!): ${id}!
          delete_${id} (id: ID!): ${id}!
        }

        type Subscription {
          get_${id} (id: ID!): ${id}!
          list_${id} (where: Model${id}WhereInput, distinctOn: [Model${id}ColumnInput], limit: Int, offset: Int, orderBy: Model${id}OrderByInput): [${id}]!
        }`}
        `;
            }),
        ].join("\n");
    }

    convertGraph = function (field, table, args = {}, variables = {}, headers, child = false) {
        return {
            ...(child ? {} : { schema: table.schema, headers }),
            schema: table.schema,
            target: [field.alias ? field.alias.value : field.name.value, child ? field.name.value : table.id],
            variables: parseArguments(field.arguments, variables),
            fields: field.selectionSet?.selections.filter((_field) => !_field.selectionSet).map((_field) => (_field.alias ? [_field.alias.value, _field.name.value] : [_field.name.value])),
            links: field.selectionSet?.selections.filter((_field) => _field.selectionSet).map((_field) => this.convertGraph(_field, this.tables.get((table.columns.filter((col) => `list_${col.type}` == _field.name.value)[0] || { type: _field.name.value }).type), args, variables, headers, true)),
        };
    };

    generateResolves() {
        const tables = this.tables;
        let all_action = [];
        [...this.tables]
            .map(([key, val]) => val)
            .filter(({ id, schema, column }) => !column && !id.endsWith("_history") && (schema === this.path))
            .forEach(({ id, columns }) => {
                all_action = [
                    ...all_action,
                    ...[
                        ["query", "get"],
                        ["query", "list"],
                        ["query", "aggregate"],
                        ["mutation", "create"],
                        ["mutation", "update"],
                        ["mutation", "delete"],
                        ["subscription", "get"],
                        ["subscription", "list"],
                    ].map(([mode, type]) => [
                        mode,
                        type + "_" + id,
                        async (parentValue, args, context, info) => {
                            let query = this.convertGraph(info.fieldNodes[0], this.tables.get(id), args, info["variableValues"], context.headers, false);
                            return await this.sendAndListenQueue(mode, type, query, context);
                        },
                    ]),
                ];
            });

        return {
            Query: Object.fromEntries(all_action.filter(([mode, name, func]) => mode == "query").map(([type, name, func]) => [name, func])),
            Mutation: Object.fromEntries(all_action.filter(([mode, name, func]) => mode == "mutation").map(([type, name, func]) => [name, func])), Subscription: Object.fromEntries(all_action.filter(([mode, name, func]) => mode == "subscription").map(([type, name, func]) => [name, func])),
        };
    }
}

