let createGraphqlServer = async (schema, tables) => {
  node.status({
    fill: "red",
    shape: "ring",
    text: "Start Graphql Server: " + schema
  })
  let graphQlServer = new Build_graphQlServer({
    path: schema,
    tables
  });

  const queue = async (mode, action, data, context) => {
    return new Promise((resolve, reject) => {
      emitter.emit(context.headers['transaction-id'], {
        mode, action, data, context
      });
      reject("success")
      // throw new Error({ message: "success" });
    })
  };
  graphQlServer.setQueue(queue);
  const apolloServer = new ApolloServer({
    typeDefs: graphQlServer.generateTypeDefs(),
    resolvers: graphQlServer.generateResolves(),
    introspection: true,
    cache: "bounded",
  });
  await apolloServer.start();
  graphQlServerContainer[schema] = apolloServer;
  completegraphQlServerContainer(schema);
  node.status({
    fill: "green",
    shape: "ring",
    text: "End Graphql Server: " + schema
  })
}



context.set("createGraphqlServer", createGraphqlServer)
context.set("getGraphQlServer", async ({ schema, payload, options }) => {
  return await new Promise( async ( resolve, reject ) => {
    if (!graphQlServerContainer[schema]) {
      resolve({
        topic: "error",
        payload: {
          type: "error",
          payload: {
            errors: [{
              message: "Not found schema: " + schema
            }]
          }
        }
      });
      return
    }
    let transactionId = createId();
    options.contextValue.headers['transaction-id'] = transactionId;
    emitter.once(transactionId, res => {
      resolve({
        topic: "parse",
        payload: res
      })
    });
    let result = await graphQlServerContainer[schema].executeOperation(payload, options).catch(err => {
      return {
        body: {
          singleResult: {
            errors: true,
            payload: err.message
          }
        }
      };
    });
    let resData = result?.body?.singleResult || {};
    if (resData?.errors) {
      resolve({
        topic: "error",
        payload: { type: 'error', payload: resData }
      })
    } else {
      resolve({
        topic: "data",
        payload: { type: 'data', payload: resData }
      })
    };
    return null
  })
})


