const filterPrismaToHasuraInWhere = new Map([
     ["neq", ["_neq"]],
     ["eq", ["_eq"]],
     ["equals", ["_eq"]],
     ["lte", ["[_lte"]],
     ["lt", ["_lt"]],
     ["gte", ["_gte"]],
     ["gt", ["_gt"]],
     ["in", ["_in"]],
     ["notIn", ["_nin"]],
     ["like", ["_like"]],
     ["iLike", ["_ilike"]],
     ["regex", ["_regex"]],
     ["iRegex", ["_iregex"]],
     ["hasKey", ["_has_key"]],
     ["isNull", ["_is_null"]],
 ]);

const filterPrismaToHasura = new Map([
       ["neq", ["_neq"]],
       ["eq", ["_eq"]],
       ["equals", ["_eq"]],
       ["lte", ["[_lte"]],
       ["lt", ["_lt"]],
       ["gte", ["_gte"]],
       ["gt", ["_gt"]],
       ["in", ["_in"]],
       ["notIn", ["_nin"]],
       ["like", ["_like"]],
       ["iLike", ["_ilike"]],
       ["regex", ["_regex"]],
       ["iRegex", ["_iregex"]],
       ["and", ["_and"]],
       ["or", ["_or"]],
       ["not", ["_not"]],
       ["distinctOn", ["distinct_on"]],
       ["orderBy", ["order_by"]],
       ["hasKey", ["_has_key"]],
       ["cast", ["_cast"]],
       ["String", ["String"]],
       ["path", ["path"]],
   ]);

const generatedHasuraVariables = (objectQuery, type = undefined, zone = '', level = '') => {
    // objectQuery.variables.where = { and: [objectQuery.variables.where || {}, { deletedAt: { isNull: true } }] }
    let generatedHasuraVariablesZone = (variables, type = undefined) => {
        return Object.fromEntries(Object.entries(variables).map(([key, val]) => {
            let test_key = filterPrismaToHasuraInWhere.get(key);
            if (test_key) {
                return [test_key, val]
            } else {
                let _type = type || key;
                let new_key = [...filterPrismaToHasura.get(key) || [key]][0];
                if (val && ["and", "or", "not"].includes(key) && Object.prototype.toString.call(val) === '[object Array]') {
                    return [new_key, val.map(_val => generatedHasuraVariablesZone(_val, _type))]
                } else if (val && !filterPrismaToHasuraInWhere.has(key) && Object.prototype.toString.call(val) === '[object Object]') {
                    if (new_key.startsWith("list")) {
                        return [new_key, generatedHasuraVariablesZone({ and: [{ deletedAt: { isNull: true } }, val] }, _type)]
                    } else {
                        return [new_key, generatedHasuraVariablesZone(val, _type)]
                    }
                } else {
                    return [new_key, val]
                }
            }
        }))
    }
    return Object.fromEntries([...Object.entries(generatedHasuraVariablesZone(objectQuery.variables)).map(([key, val]) => [`${key}_${zone}_${level}`, val]),
    ...objectQuery.links.map((childQuery, childQueryIndex) => Object.entries(generatedHasuraVariables(childQuery, undefined, `${zone}${level}`, childQueryIndex))).flat()
    ])
}

const target_begin = {
    get: ``,
    list: ``,
    aggregate: ``,
    create: 'insert_',
    update: "update_",
    delete: "delete_"
}

const target_end = {
    get: ``,
    list: ``,
    aggregate: `_aggregate`,
    create: '_one',
    update: "_by_pk",
    delete: "_by_pk"
}

const generatedHasuraQuery = (type, action, objectQuery, root = true, zone = '', level = '') => {

    return `${(["socket", "subscription"].includes(type)) ? `${[...objectQuery.target].shift()}:` : (root ? "data:" : "")}${objectQuery.schema ? `${root ? `${target_begin[action]}${objectQuery.schema}_` : ""}${objectQuery.target.slice(-1)}${target_end[action]}` : objectQuery.target.slice(-1)[0]}
    ${Object.keys(objectQuery.variables).length > 0 ? `(${(() => {
            let trans_variables = Object.keys(objectQuery.variables).map(k => (filterPrismaToHasura.get(k) || [k])[0]);
            return [
                ["where", `$where_${zone}_${level}`],
                ["order_by", `$order_by_${zone}_${level}`],
                ["distinct_on", `$distinct_on_${zone}_${level}`],
                ["limit", `$limit_${zone}_${level}`],
                ["offset", `$offset_${zone}_${level}`],
                ["createInput", `$createInput_${zone}_${level}`, "object"],
                ["updateInput", `$updateInput_${zone}_${level}`, "_set"],
                ["pk_columns", `$pk_columns_${zone}_${level}`],
                ["id", `$id_${zone}_${level}`]
            ].filter(([key, val]) => trans_variables.includes(key)).map(([originKey, newVal, newKey]) => `${newKey || originKey}: ${newVal}`).join(", ")

        })()
            })` : ""} {
            ${objectQuery.fields.map((x) => `${(x.length > 1 && ["socket", "subscription"].includes(type)) ? `${x.slice(0, 1)[0]}:` : ""}${x.slice(-1)[0]}`).join("\n")}
            ${objectQuery.links.map((x, linkIndex) => generatedHasuraQuery(type, action, x, false, `${zone}${level}`, linkIndex))}
        }`;
}

const generateHasuraVariablesType = (objectQuery, zone = '', level = '') => {
    let table = `${objectQuery.schema}_${[...objectQuery.target].pop().replace("list_", "")}`
    let keys = Object.keys(objectQuery.variables).map(k => (filterPrismaToHasura.get(k) || [k])[0]);
    let tt = [...[
        ["where", `${table}_bool_exp!`],
        ["order_by", `[${table}_order_by!]`],
        ["distinct_on", `[${table}_select_column!]`],
        ["updateInput", `${table}_set_input`],
        ["createInput", `${table}_insert_input!`],
        ["limit", `Int`],
        ["offset", `Int`],
        ["pk_columns", `${table}_pk_columns_input!`],
        ["id", `String!`]
    ].filter(([key, val]) => keys.includes(key)).map(([key, val]) => `$${key}_${zone}_${level}: ${val}`),
    ...objectQuery.links.map((childQuery, childQueryIndex) => generateHasuraVariablesType(childQuery, `${zone}${level}`, childQueryIndex))
    ]
    return tt.filter(x => x).join(",")
}

const generateHasuraObject = (id, mode, action, objectQuery, root = true) => {
    switch (action) {
        case "create":
            objectQuery.variables["createInput"] = objectQuery.variables.input;
            delete objectQuery.variables.input;
            break;
        case "update":
            objectQuery.variables["pk_columns"] = { id: objectQuery.variables.id };
            delete objectQuery.variables.id;
