            case 'error':
                res.status(400)
                res.send(payload)
                break;
        }
        res.end();
    });
    addJob(requestId, {
        id: requestId,
        schema: req?.params?.schema,
        payload: req.body,
        context: { 
            headers: req.headers,
            id: requestId,
            replyTo: returnQueue
         }
    }, { jobId: requestId })
};

