const processSocketRequest = (socket) => {
  // Khi một client kết nối thành công vào một namespace khớp với mẫu regex
  console.log(`User connected to a dynamic namespace: ${socket.nsp.name}`);

  socket.on('data', ({ id, type, payload }) => {
    try {
      console.log('sock', id, type, payload)
      let path = socket.nsp.name;
      let requestId = paralleldriveCuid2.createId();
      if (type === "connection_init") socket.compress(true).emit('data', { id, type: 'connection_ack' });
      else if (["query", "subscription", "start", "patch"].includes(type)) {
        addJob(requestId, {
          id: requestId,
          schema: path.split('/').slice(-1)[0],
          payload: JSON.parse(payload),
          context: {
            headers: {},
            id: requestId,
            replyTo: returnQueue
          }
        }, { jobId: requestId });
        emitter.on(requestId, ({ type: tRes, payload: pRes }) => {
            if (tRes == "data") {
                socket.compress(true).emit('data', { id, type: 'data', payload: pRes });
            } else {
              socket.compress(true).emit('data', { id, type: tRes, payload: pRes });
            }
        });
      } else if (["stop", "complete"].includes(type)) {
        socket.compress(true).emit('data', { id, type });
      } else {
          socket.compress(true).emit('data', { id, type: 'error', payload: { errors: [{ message: "'type' is not defined!" }] } });
      }
    } catch (err) {
      socket.compress(true).emit('data', { id, type: 'error', payload: { errors: [{ message: err.message }] } });
    }

    console.log(`Message from ${socket.nsp.name}:`);
    // Bạn có thể phát sự kiện trở lại chỉ cho namespace đó
    // socket.nsp.emit('message', `Echo from ${socket.nsp.name}: ${msg}`);
  });

  socket.on('disconnect', () => {
    console.log(`User disconnected from a dynamic namespace: ${socket.nsp.name}`);
  });
}

