let connectSocket = async () => {
    let hasuraURL = new url.URL(env.get("HASURA_URL"));
    // Convert Ws Query
    hasuraURL.protocol = "ws:";
    hasuraURL.pathname = "/v1/graphql";
    console.log(`Connecting Hasura: ${hasuraURL.href}`);
    wsApp = new WebSocket(hasuraURL.href, ["graphql-ws"]);
    wsApp.on("open", async () => {
        console.log(`Connect Hasura successfully: ${hasuraURL.href}`);
        await wsApp.send(JSON.stringify({ type: "connection_init", payload: { headers: { "content-type": "application/json" }, lazy: true } }));
    });

    wsApp.on("message", async (msg) => {
        const { id, type, payload } = JSON.parse(msg.toString());
        console.log("message", id, type, payload?.errors);
        if (id) {
            let has_listener = await emitter.emit(id, {
                type,
                payload
            });
            if (!has_listener) {
                emitter.emit(`stop-${id}`, null)
            }
        } else if (type === "connection_ack") {
            wsAppConnected = true;
            emitter.emit("connect", true);
        }
    });


    wsApp.on("error", (err) => {
        console.log("Connect Hasura failed!");
    });

    wsApp.on("close", () => {
        console.log("Connect Hasura Socket closed!");
        console.log(`Try reconnect to Hasura after: 1000ms`);
        if (this.state !== "close") {
            setTimeout(connectSocket, 1000)
        }
    });
};

